import React from 'react';
import { connect } from 'react-redux';
import { Link, NavLink } from 'react-router-dom';
import { logoutUser } from '../../actions/auth-action';

const Navbar = ({ cooler, logoutUser, isAuthenticated, user, loading }) => {
  const logoutHandler = () => {
    logoutUser();
  };

  const authLinks = (
    <ul>
      <li>
        <NavLink to='/my-profile'>
          <i className='fas fa-lg fa-user' /> {user && user.name}
        </NavLink>
      </li>
      <li>
        {cooler === 0 ? (
          <NavLink to='/cooler'>
            <i className='fas fa-lg fa-shopping-basket'></i> My Cooler
          </NavLink>
        ) : (
          <NavLink to='/cooler'>
            <i className='fas fa-lg fa-shopping-basket'></i> My Cooler ({cooler}
            )
          </NavLink>
        )}
      </li>
      <li>
        <Link to='/' onClick={logoutHandler}>
          <i className='fas fa-lg fa-sign-out-alt' />{' '}
          <span className='hide-sm'>Logout</span>
        </Link>
      </li>
    </ul>
  );

  const guestLinks = (
    <ul>
      <li>
        <NavLink to='/login'>
          <i className='fas fa-sign-in-alt'></i> Login
        </NavLink>
      </li>
      <li>
        <NavLink to='/signup'>
          <i className='fas fa-user-plus'></i> Signup
        </NavLink>
      </li>
      <li>
        {cooler === 0 ? (
          <Link to='/cooler'>
            <i className='fas fa-lg fa-shopping-basket'></i> My Cooler
          </Link>
        ) : (
          <Link to='/cooler'>
            <i className='fas fa-lg fa-shopping-basket'></i> My Cooler ({cooler}
            )
          </Link>
        )}
      </li>
    </ul>
  );

  return (
    <nav className='navbar bg-dark'>
      <h2>
        <Link to='/beers'>
          <i className='fas fa-beer'></i> BeerCooler{' '}
        </Link>
      </h2>
      {isAuthenticated ? authLinks : !loading && guestLinks}
    </nav>
  );
};

const mapStateToProps = (state) => ({
  cooler: state.cooler.cooler.length,
  isAuthenticated: state.auth.isAuthenticated,
  user: state.auth.user,
  loading: state.auth.loading,
});

export default connect(mapStateToProps, { logoutUser })(Navbar);
